-- Functions
CREATE FUNCTION public.update_item_status() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    -- Update the status for the affected item
    UPDATE items 
    SET status = (CASE 
        WHEN (SELECT COALESCE(SUM(quantity), 0) FROM item_locations WHERE item_id = 
              CASE 
                WHEN TG_OP = 'DELETE' THEN OLD.item_id 
                ELSE NEW.item_id 
              END) = 0 THEN 'out_of_stock'
        WHEN (SELECT COALESCE(SUM(quantity), 0) FROM item_locations WHERE item_id = 
              CASE 
                WHEN TG_OP = 'DELETE' THEN OLD.item_id 
                ELSE NEW.item_id 
              END) <= minimum_stock THEN 'low_stock'
        ELSE 'in_stock'
    END)::item_status
    WHERE id = CASE 
        WHEN TG_OP = 'DELETE' THEN OLD.item_id 
        ELSE NEW.item_id 
    END;
    
    RETURN COALESCE(NEW, OLD);
END;
$$;

